package test.hibernate30;import java.util.Collection;/** * @hibernate.subclass *     table="PETS" *     discriminator-value="pet" */public class Pet extends Product {	private Animal animal;		private Collection lovers;	/**	 * Constructor for Pet.	 */	public Pet() {		super();	}	/**	 * @hibernate.one-to-one	 * @return Animal	 */	public Animal getAnimal() {		return animal;	}	/**	 * Sets the animal.	 * @param animal The animal to set	 */	public void setAnimal(Animal animal) {		this.animal = animal;	}		/**	 * @hibernate.idbag	 *     table="PEOPLE"	 *     	 *     @hibernate.collection-id	 *         column="ID"	 *         type="long"	 * 	 *         @hibernate.generator	 *             class="sequence"	 *         	 *     @hibernate.key	 *         column="ID"	 *         	 *     @hibernate.many-to-many	 *         column="LOVER"	 *         class="test.hibernate30.Person"	 *         outer-join="true"
	 */	public Collection getLovers() {		return this.lovers;	}	public void setLovers(Collection lovers) {		this.lovers = lovers;	}}